import React, { Component } from 'react'


import classes from './Table.css';

this.state = {};
//Columns defines table headings and properties to be placed into the table
let columns = [
  {
    heading: 'Item',
    inputType: 'Select'
  },
  {
    heading: 'Material Fee',
    inputType: 'currency'
  },
  {
    heading: 'Packing Fee',
    inputType: 'currency'
  },
  {
    heading: 'Unpacking Fee',
    inputType: 'currency'
  },
]


class Table extends Component {



  buildTable = (columns,  key) => {
    let headerRow = [];
    //Build the table header
    columns.forEach (col => {
      headerRow.push(
        <th key={`header-${col.heading}`}>{col.heading}</th>
      );
    });



    return(
      <>
        <thead>
          <tr>{headerRow}</tr>
        </thead>
        <tbody>

        </tbody>
      </>
    );
  };




  render() {
        var onTableUpdate = this.props.onTableUpdate;
    var rowDel = this.props.onRowDel;
    const {
      columns,
      propertyAsKey
    } = this.props;

    return (
      <table className={classes.table}>
        {this.buildTable(columns,propertyAsKey)}
      </table>
    );
  }
}

export default Table;
