import React, { Component } from 'react';
import ItemRow from './ItemRow/ItemRow';
import classes from '../Table.css';

 
class ItemTable extends React.Component {
   
 renderHeader = () => {
    let header = [];
    this.colomns.forEach(colomn => {
      header.push(" " + colomn.heading);
    })
     return header.map((key, index) => {
      return <th key={index}>{key.toUpperCase()}</th>
    })

  };

 render() {
    var onItemTableUpdate = this.props.onItemTableUpdate;
    var rowDel = this.props.onRowDel;
    var filterText = this.props.filterText;
    var item = this.props.items.map(function(item) {
      if (item.name.indexOf(filterText) === -1) {
        return;
      }
      return (<ItemRow onItemTableUpdate={onItemTableUpdate} item={item} onDelEvent={rowDel.bind(this)} key={item.id}/>)
    });
    return (
      <div>


      <button type="button" onClick={this.props.onRowAdd} className="btn btn-success pull-right">Add</button>
        <table className="table table-bordered">
          <thead>
            <tr>
              <th>Name</th>
              <th>price</th>
              <th>quantity</th>
              <th>category</th>
            </tr>
          </thead>

          <tbody>
            {item}

          </tbody>

        </table>
      </div>
    );

  }

}


export default ItemTable;
