import React, { Component } from "react";
import classes from "../../../Button/Button.scss";
import Input from "../../../Input/Input";
import TableRow from '@material-ui/core/TableRow';
import Box from '@material-ui/core/Box';

const defaultProps = {
  bgcolor: 'background.paper',
  borderColor: 'text.primary',
  m: 1,
  border: 1,
  style: { width: '75rem', height: '2rem' },
};
class ItemRow extends Component {
  state = {
    tablerow: {
      itemname: {
        elementType: "select",
        elementConfig: {
          options: [
            { value: "itemvalue1", displayValue: "itemvalue1" },
            { value: "itemvalue2", displayValue: "itemvalue2" },
            { value: "itemvalue3", displayValue: "itemvalue3" },
            { value: "itemvalue4", displayValue: "itemvalue4" }
          ]
        },
             },
      materialfees: {
        elementType: "input",
        elementConfig: {
          type: "text",
          placeholder: "0"
        },
             },
      packingfees: {
        elementType: "input",
        elementConfig: {
          type: "text",
          placeholder: "0"
        },
       
      },
      unpackingfees: {
        elementType: "input",
        elementConfig: {
          type: "text",
          placeholder: "0"
        },
       
      }
    },
     };


 onDelEvent() {
    this.props.onDelEvent(this.props.item);
    

  }

  renderInput = () => {
       const trowElementsArray = [];
    for (let key in this.state.tablerow) {
    trowElementsArray.push({
      id: key,
      config: this.state.tablerow[key]
      });
    }
    let trow = (
     <>
        {trowElementsArray.map(trowElement => (
          <td className={classes.td} key={trowElement.id}>
            <Input
              key={trowElement.id}
              elementType={trowElement.config.elementType}
              elementConfig={trowElement.config.elementConfig}
              value={trowElement.config.value}  
               autoFocus  
            />
          </td>
        ))}
        <td>
          <button onClick={i => this.onDelEvent(i)} className="{button}">X</button>
        </td>
      </>
    );
    return (<Box borderRadius={16} {...defaultProps} ><TableRow hover >{trow}</TableRow></Box>);
  };
  render() {  
    return(<>{this.renderInput()}</>);
  }
}
export default ItemRow;
