import React, { Component } from "react";

import ItemTable from "./ItemTable/ItemTable";

import classes from './Table.css';

class Table extends Component {
  constructor(props) {
        super(props);
    this.state = {};
    this.state.rows = [];
   
  }

  
   handleRowDelete(row) {
    var index = this.state.rows.indexOf(row);
    this.state.rows.splice(index, 1);
    this.setState(this.state.rows);
  };
  handleItemAdd = () => {
    const colomns = this.colomns.map(colomn => {
      return {
        name: colomn.heading,
        inputType: colomn.inputType,
        value: ""
      };
    });

    const newItem = {
      key: "asksakdaskd",
      colomns: colomns
    };
var rows = this.state.rows
        rows.push(newItem)
        this.setState({rows: rows})
      
 //  this.setState({ rows: [...this.state.rows, newItem] });
  };
 
  renderTable = (evt) => {
  var item = {
      id: evt.target.id,
      name: evt.target.name,
      value: evt.target.value
    };
var items = this.state.items.slice();
  var newItems = items.map(function(item) {

    for (var key in item) {
      if (key == item.name && item.id == item.id) {
        item[key] = item.value;

      }
    }
    return item;
  });
    this.setState({items:newItems});
  };
 
render (){
    return (
      <div>      
        <ItemTable onItemTableUpdate={this.renderTable.bind(this)} onRowAdd={this.handleItemAdd.bind(this)} onRowDel={this.handleRowDelete.bind(this)} items={this.state.items} filterText={this.state.filterText}/>
      </div>
    );
}
}

export default Table;
