import React, { Component } from "react";

import ItemTable from "./ItemTable/ItemTable";

import classes from "../Button/Button.css";

class Table extends Component {
  constructor(props) {
    super(props);

    this.state = {
      rows: []
    };

    this.colomns = [
      {
        heading: "Item",
        inputType: "Select"
      },
      {
        heading: "Material Fee",
        inputType: "currency"
      },
      {
        heading: "Packing Fee",
        inputType: "currency"
      },
      {
        heading: "Unpacking Fee",
        inputType: "currency"
      }
    ];
  }

  handleItemAdd = () => {
    const colomns = this.colomns.map(colomn => {
      return {
        name: colomn.heading,
        inputType: colomn.inputType,
        value: ""
      };
    });

    const newItem = {
      key: "asksakdaskd",
      colomns: colomns
    };

    this.setState({ rows: [...this.state.rows, newItem] });
  };

  handleDelete = item => {
    const filterdRows = this.state.rows.filter(row => row.key !== item.key);
    this.setState({ rows: filterdRows });
  };

  renderHeader = () => {};

  renderRows = () => {

  };

  renderFooter = () => {
    
  };

  renderTable = () => {
    return (
      <table>
        {this.renderHeader()}
        {this.renderRows()}
      </table>
    );
  };

  render() {
    return (
      <div>
        {this.renderTable()}
        {this.renderFooter()}
      </div>
    );
  }
}

export default Table;
