import React, { Component } from "react";
import classes from "../../Button/Button.css";
import Input from "../../Input/Input";

class ItemRow extends Component {
  state = {
    tablerow: {
      itemname: {
        elementType: "select",
        elementConfig: {
          options: [
            { value: "itemvalue1", displayValue: "itemvalue1" },
            { value: "itemvalue2", displayValue: "itemvalue2" },
            { value: "itemvalue3", displayValue: "itemvalue3" },
            { value: "itemvalue4", displayValue: "itemvalue4" }
          ]
        },
        
      },
      materialfees: {
        elementType: "input",
        elementConfig: {
          type: "text",
          placeholder: "0"
        },
       
      },
      packingfees: {
        elementType: "input",
        elementConfig: {
          type: "text",
          placeholder: "0"
        },
        
      },
      unpackingfees: {
        elementType: "input",
        elementConfig: {
          type: "text",
          placeholder: "0"
        },
      
      }
    },
   
  };
    renderFooter = () => {
     return <td><button type="button" onClick={this.handleItemAdd} className={classes.Button}>Add Item</button></td>
  };
handleItemAdd = () => {
    const colomns = this.colomns.map(colomn => {
      return {
        name: colomn.heading,
        inputType: colomn.inputType,
        value: ""
      };
    });

    const newItem = {
      key: "asksakdaskd",
      colomns: colomns
    };
var rows = this.state.rows
        rows.push(newItem)
        this.setState({rows: rows})
      
 //  this.setState({ rows: [...this.state.rows, newItem] });
  };
  renderInput = () => {
      
  }

  render() {
    const trowElementsArray = [];
    for (let key in this.state.tablerow) {
      trowElementsArray.push({
        id: key,
        config: this.state.tablerow[key]
      });
    }
    let trow = (
      <tr>
        {trowElementsArray.map(trowElement => (
          <td className={classes.td} key={trowElement.id}>
            <Input
              key={trowElement.id}
              elementType={trowElement.config.elementType}
              value={trowElement.config.value} />
          </td>
        ))}
        <td>
          <button onClick={event => this.removeRow(event, trow)}>X</button>
        </td>
      </tr>
    );
    return <>{trow}</>;
  }
}
export default ItemRow;
