import React, { Component } from 'react'


import classes from './Table.css';

class Table extends Component {

constructor(props) {
    super(props);

this.state = {};
//Columns defines table headings and properties to be placed into the table


this.state.columns = [
  {
    heading: 'Item',
    inputType: 'Select'
  },
  {
    heading: 'Material Fee',
    inputType: 'currency'
  },
  {
    heading: 'Packing Fee',
    inputType: 'currency'
  },
  {
    heading: 'Unpacking Fee',
    inputType: 'currency'
  },
]
}

handleRowDel(column) {
    var index = this.state.columns.indexOf(column);
    this.state.columns.splice(index, 1);
    this.setState(this.state.columns);
  };

  handleAddEvent(evt) {
    var id = (+ new Date() + Math.floor(Math.random() * 999999)).toString(36);
    var column = {
      id: id,
      Item: "",
      MaterialFee:  0,
      PackingFee:  0,
      UnpackingFee: 0
    }
    this.state.columns.push(column);
    this.setState(this.state.column);

  }

  handleProductTable(evt) {
    var item = {
      id: evt.target.id,
      name: evt.target.name,
      value: evt.target.value
    };
var columns = this.state.columns.slice();
  var newColumns = columns.map(function(column) {

    for (var key in column) {
      if (key == item.name && column.id == item.id) {
        column[key] = item.value;

      }
    }
    return column;
  });
    this.setState({columns:newColumns});
  
  };
  render() {

    return (
      <div>
        <SearchBar filterText={this.state.filterText} onUserInput={this.handleUserInput.bind(this)}/>
        <ProductTable onProductTableUpdate={this.handleProductTable.bind(this)} onRowAdd={this.handleAddEvent.bind(this)} onRowDel={this.handleRowDel.bind(this)} products={this.state.products} filterText={this.state.filterText}/>
      </div>
    );

  }

}

export default Table;
