import React, { Component } from 'react';

import ItemRow from './ItemRow/ItemRow';

import classes from '../Table.css';


class ItemTable extends Component {


addNewRow(evt) {
    var id = (+ new Date() + Math.floor(Math.random() * 999999)).toString(36);
    var tablerowc = {
      itemname: "",
      materialfees: 0,
      packingfees: 0,
      unpackingfees:0
    }
    this.state.tablerow.push(tablerowc);
    this.setState(this.state.tablerow);

  }

 RowDelete(tablerows) {
    var index = this.state.tablerow.indexOf(tablerows);
    this.state.tablerow.splice(index, 1);
    this.setState(this.state.tablerow);
  };

 renderTableHeader() {

    let header = [];
    this.props.items.forEach(item => {
      header.push(" " + item.heading);
    })


    return header.map((key, index) => {
      return <td key={index}>{key.toUpperCase() }</td>
    })
  }

  render() {
       return (
      <div>

         <table className={classes.table}>
        <thead>
            <tr>
              {this.renderTableHeader() }
              </tr>
            </thead>
            <tbody>
      <ItemRow onRowAdd={this.addNewRow.bind(this)}  onRowDel={this.RowDelete.bind(this)}/>
         </tbody>

        </table>
     
      </div>
    );

  }

}

export default ItemTable;
