import React, { Component } from "react";

import ItemRow from "./ItemRow/ItemRow";
import classes from './Table.css';

class Table extends Component {
  constructor(props) {
        super(props);
   
        this.state = {
          rows: []
        }
  

    this.colomns = [
      {
        heading: "Item",
        inputType: "Select"
      },
      {
        heading: "Material Fee",
        inputType: "currency"
      },
      {
        heading: "Packing Fee",
        inputType: "currency"
      },
      {
        heading: "Unpacking Fee",
        inputType: "currency"
      }
    ];
  }

  

  handleDelete = item => {
    const filterdRows = this.state.rows.filter(row => row.key !== item.key);
    this.setState({ rows: filterdRows });
  };

  renderHeader = () => {
    let header = [];
    this.colomns.forEach(colomn => {
      header.push("" + colomn.heading);
    })
     return header.map((key, index) => {
      return <th key={index}>{key.toUpperCase()}</th>
    })

  };



  renderFooter = () => {
     return <td><button type="button" onClick={handleItemAdd()} className={classes.Button}>Add Item</button></td>
  };

  renderTable = () => {
    return (
      <table><thead><tr>{this.renderHeader()}</tr></thead><tbody><tr>{this.renderFooter()}</tr></tbody></table>
    );
  };

  render() {
    return (
      <div>
     
        {this.renderTable()} 
      
      </div>
    );
  }
}

export default Table;
