    this.setState(this.state.item);

  }

   listItemTable(evt) {
    var rowitem = {
      id: evt.target.id,
      name: evt.target.name,
      value: evt.target.value
    };
var items = this.state.items.slice();
  var newItems = items.map(function(item) {

    for (var key in item) {
      if (key == rowitem.name && item.id == rowitem.id) {
        item[key] = rowitem.value;

      }
    }
    return item;
  });
    this.setState({items:newItems});

  };
    
  render() {

    return (
      <div>
       
        <ItemTable onItemTableUpdate={this.listItemTable.bind(this)} onRowAdd={this.tableAddRowItem.bind(this)} onRowDel={this.tableRowDelItem.bind(this)} items={this.state.items} />
      </div>
    );

  }

}

export default Table;
