import React, { Component } from "react";

import ItemTable from "./ItemTable/ItemTable";

import classes from './Table.css';

class Table extends Component {
  constructor(props) {
        super(props);
    this.state = {};
    this.state.items = [];
     this.colomns = [
      {
        heading: "Item",
        inputType: "Select"
      },
      {
        heading: "Material Fee",
        inputType: "currency"
      },
      {
        heading: "Packing Fee",
        inputType: "currency"
      },
      {
        heading: "Unpacking Fee",
        inputType: "currency"
      },
      {
        heading: "",
        inputType: ""
      }
    ];
   
  }

  
    handleRowDel(item) {
    var index = this.state.items.indexOf(item);
    this.state.items.splice(index, 1);
    this.setState(this.state.items);
  };

  handleAddEvent(evt) {
    var id = (+ new Date() + Math.floor(Math.random() * 999999)).toString(36);
    var item = {
      id: id,
      name: "",
      price: "",
      category: "",
      qty: 0
    }
    this.state.items.push(item);
    this.setState(this.state.items);

  }

  handleItemTable(evt) {
    var item = {
      id: evt.target.id,
      name: evt.target.name,
      value: evt.target.value
    };
var items = this.state.items.slice();
  var newItems = items.map(function(item) {

    for (var key in item) {
      if (key == item.name && item.id == item.id) {
        item[key] = item.value;

      }
    }
    return item;
  });
    this.setState({items:newItems});
  //  console.log(this.state.items);
  };
  render() {

    return (
      <div>
        <SearchBar filterText={this.state.filterText} onUserInput={this.handleUserInput.bind(this)}/>
        <ItemTable onItemTableUpdate={this.handleItemTable.bind(this)} onRowAdd={this.handleAddEvent.bind(this)} onRowDel={this.handleRowDel.bind(this)} items={this.state.items} filterText={this.state.filterText}/>
      </div>
    );

  }

}
export default Table;
