import React, { Component } from 'react';
import ItemRow from './ItemRow/ItemRow';
import classes from "../../Table/Table.css";
 
class ItemTable extends Component {
   
 renderHeader = () => {
    let header = [];
    this.colomns.forEach(colomn => {
      header.push(" " + colomn.heading);
    })
     return header.map((key, index) => {
      return <th key={index}>{key.toUpperCase()}</th>
    })

  };

renderFooter = () => {
     return  <tr><td> <button type="button" onClick={this.props.onRowAdd} className="{classes.button}">Add Item</button> </td></tr>
  };

  renderInput = () => {


  };

 render() {
    var onItemTableUpdate = this.props.onItemTableUpdate;
    var rowDel = this.props.onRowDel;
     var item = this.props.items.map(function(item) {
     
      return (<ItemRow onItemTableUpdate={onItemTableUpdate} item={item} onDelEvent={rowDel.bind(this)} key={item.id}/>)
    });
    return (
      <div>


      
        <table className="{classes.table}">
          <thead>
            <tr>
              <th>Item</th>
              <th>Material Fee</th>
              <th>Packing Fee</th>
              <th>Unpacking Fee</th>
            </tr>
          </thead>

          <tbody>
            {item}
           {this.renderFooter()}
          </tbody>
        
          
        </table>
      </div>
    );

  }

}


export default ItemTable;
