import React, { Component } from "react";

import ItemTable from "./ItemTable/ItemTable";

import classes from './Table.css';

class Table extends Component {
  constructor(props) {
        super(props);
    this.state = {};
    this.state.rows = [];
       
  

    this.colomns = [
      {
        heading: "Item",
        inputType: "Select"
      },
      {
        heading: "Material Fee",
        inputType: "currency"
      },
      {
        heading: "Packing Fee",
        inputType: "currency"
      },
      {
        heading: "Unpacking Fee",
        inputType: "currency"
      },
      {
        heading: "",
        inputType: ""
      }
    ];
  }

  
   handleDelete(row) {
    var index = this.state.rows.indexOf(row);
    this.state.rows.splice(index, 1);
    this.setState(this.state.rows);
  };
  handleItemAdd = () => {
    const colomns = this.colomns.map(colomn => {
      return {
        name: colomn.heading,
        inputType: colomn.inputType,
        value: ""
      };
    });

    const newItem = {
      key: "asksakdaskd",
      colomns: colomns
    };
var rows = this.state.rows
        rows.push(newItem)
        this.setState({rows: rows})
      
 //  this.setState({ rows: [...this.state.rows, newItem] });
  };
  renderHeader = () => {
    let header = [];
    this.colomns.forEach(colomn => {
      header.push(" " + colomn.heading);
    })
     return header.map((key, index) => {
      return <th key={index}>{key.toUpperCase()}</th>
    })

  };
  renderTable = () => {
    return (
      <table><thead><tr>{this.renderHeader()}</tr></thead><tbody><ItemTable /></tbody></table>
    );
  };
 

  render() {
     
    return (
     { renderTable}
    );
  }
}

export default Table;
