import React, { Component } from 'react';
import ItemRow from './ItemRow/ItemRow';
import classes from "../Table.scss";

class ItemTable extends Component {
  renderHeader = () => {
    let header = [];
    this.props.colomns.forEach(colomn => {
      header.push(" " + colomn.heading);
    })
    return header.map((key, index) => {
      return <th key={index}>{key.toUpperCase()}</th>
    })
  };
  renderFooter = () => {
    return <tr><td><button type="button" onClick={this.props.onRowAdd} className="{button}">Add Item</button></td></tr>
  };
  renderItemRow = () => {  
    var rowDel = this.props.onRowDel;
    var item = this.props.items.map(function (item) {
      return (<ItemRow item={item} onDelEvent={rowDel.bind(this)} key={item.id}/>)
    });
    return (
      <table className="{classes.table}"><thead><tr>{this.renderHeader()}</tr></thead><tbody>{item}{this.renderFooter()}</tbody></table>
    );
  };
  render() {
    return (
      <div>{this.renderItemRow()}</div>
    );
  }
}
export default ItemTable;
