import React, { Component } from "react";
import classes from "../../../Button/Button.css";
import Input from "../../../Input/Input";

class ItemRow extends Component {
  state = {
    tablerow: {
      itemname: {
        elementType: "select",
        elementConfig: {
          options: [
            { value: "itemvalue1", displayValue: "itemvalue1" },
            { value: "itemvalue2", displayValue: "itemvalue2" },
            { value: "itemvalue3", displayValue: "itemvalue3" },
            { value: "itemvalue4", displayValue: "itemvalue4" }
          ]
        },
        value: "",
        validation: {},
        valid: true
      },
      materialfees: {
        elementType: "input",
        elementConfig: {
          type: "text",
          placeholder: "0"
        },
        value: "",
        validation: {
          required: true
        },
        valid: false,
        touched: false
      },
      packingfees: {
        elementType: "input",
        elementConfig: {
          type: "text",
          placeholder: "0"
        },
        value: "",
        validation: {
          required: true,
          minLength: 5,
          maxLength: 5,
          isNumeric: true
        },
        valid: false,
        touched: false
      },
      unpackingfees: {
        elementType: "input",
        elementConfig: {
          type: "text",
          placeholder: "0"
        },
        value: "",
        validation: {
          required: true
        },
        valid: false,
        touched: false
      }
    },
    trowIsValid: false,
    loading: false
  };

renderFooter = () => {
     return  <tr><td><button type="button" onClick={this.handleItemAdd} className={classes.Button}>Add Item</button></td></tr>
  };


 onDelEvent() {
    this.props.onDelEvent(this.props.row);

  }

  renderInput = () => {
   
        const trowElementsArray = [];
    for (let key in this.state.tablerow) {
      trowElementsArray.push({
        id: key,
        config: this.state.tablerow[key]
      });
    }
    let trow = (
     <>
        {trowElementsArray.map(trowElement => (
          <td className={classes.td} key={trowElement.id}>
            <Input
              key={trowElement.id}
              elementType={trowElement.config.elementType}
              elementConfig={trowElement.config.elementConfig}
              value={trowElement.config.value}
              changed={(event) => this.inputChangedHandler(event, trowElement.id)}
            />
          </td>
        ))}
        <td>
          <button onClick={this.onDelEvent.bind(this)}>X</button>
        </td>
      </>
    );
    return ( <tr>{trow}</tr>);
  };



  render() {
  
    return(<> {this.renderInput()} {this.renderFooter()}</>);
  }
}
export default ItemRow;
