import React, { Component } from 'react';
import classes from '../../../Button/Button.css';


function EdittableCell(props) {
   return (
    <td >
        <input type='text' name={props.cellData.type} id={this.props.cellData.id} value={this.props.cellData.value} onChange={this.props.onItemTableUpdate}/>
      </td>
  );
};

class ItemRow extends Component {
  onDelEvent() {
    this.props.onDelEvent(this.props.item);

  }
  render() {

    return (
      <tr  className={classes.tr}>
        <EdittableCell onItemTableUpdate={this.props.onItemTableUpdate} cellData={{
          "type": "Item",
          value: this.props.item.Item,
          id: this.props.item.id
        }}/>
        <EdittableCell onItemTableUpdate={this.props.onItemTableUpdate} cellData={{
          type: "MaterialFee",
          value: this.props.item.MaterialFee,
          id: this.props.item.id
        }}/>
        <EdittableCell onItemTableUpdate={this.props.onItemTableUpdate} cellData={{
          type: "PackingFee",
          value: this.props.item.PackingFee,
          id: this.props.item.id
        }}/>
        <EdittableCell onItemTableUpdate={this.props.onItemTableUpdate} cellData={{
          type: "UnpackingFee",
          value: this.props.item.UnpackingFee,
          id: this.props.item.id
        }}/>
        <td className={classes.delete}>
          <input type="button" onClick={this.onDelEvent.bind(this)} value="X" className={classes.DelButton}/>
        </td>
      </tr>
    );

  }

}
export default ItemRow;
