import React, { Component } from 'react'

import ItemTable from './ItemTable/ItemTable';


class Table extends Component {

  constructor(props) {
    super(props);

    this.state = {};
    this.state.items = [
      {
        heading: 'Item',
        inputType: 'Select'
      },
      {
        heading: 'Material Fee',
        inputType: 'currency'
      },
      {
        heading: 'Packing Fee',
        inputType: 'currency'
      },
      {
        heading: 'Unpacking Fee',
        inputType: 'currency'
      },
    ]
  }

 

  render() {

    return (
      <div>
          <ItemTable  items={this.state.items} />     
           <button type="button" onClick={this.props.onRowAdd} className={classes.Button}>Add Item</button>
      </div>
    );

  }

}

export default Table;
