import React, { Component } from 'react'

import ItemTable from './ItemTable/ItemTable';

import classes from './Table.css';

class Table extends Component {

  constructor(props) {
    super(props);

    this.state = {};
    this.state.items = [
      {
        heading: 'Item',
        inputType: 'Select'
      },
      {
        heading: 'Material Fee',
        inputType: 'currency'
      },
      {
        heading: 'Packing Fee',
        inputType: 'currency'
      },
      {
        heading: 'Unpacking Fee',
        inputType: 'currency'
      },
    ]
  }

 

  tableRowDelItem(item) {
    var index = this.state.items.indexOf(item);
    this.state.items.splice(index, 1);
    this.setState(this.state.items);
  };

  tableAddRowItem(row) {
    var id = (+ new Date() + Math.floor(Math.random() * 999999)).toString(36);
    var item = {
      id: id,
      Item: "",
      MaterialFee: 0,
      PackingFee: 0,
      UnpackingFee: 0
    }
    this.state.items.push(item);
    this.setState(this.state.item);

  }

  listItemTable(evt) {
    var rowitem = {
      id: evt.target.id,
      name: evt.target.name,
      value: evt.target.value
    };
    var items = this.state.items.slice();
    var newItems = items.map(function (item) {

      for (var key in item) {
        if (key === rowitem.name && item.id === rowitem.id) {
          item[key] = rowitem.value;

        }
      }
      return item;
    });
    this.setState({ items: newItems });

  };

  render() {

    return (
      <div>

        <table >
<thead>

            {this.renderTableHeader() }
       </thead>  
       <tbody>
          <tr>
          <ItemTable onItemTableUpdate={this.listItemTable.bind(this) } onRowAdd={this.tableAddRowItem.bind(this) } onRowDel={this.tableRowDelItem.bind(this) } items={this.state.items} />
        </tr>
         </tbody>
        </table>
      </div>
    );

  }

}

export default Table;
