import React, { Component } from 'react';
import classes from '../../../Button/Button.css';
import Input from '../../../Input/Input';


class ItemRow extends Component {


    state = {
        tablerow: {
            itemname: {
                elementType: 'select',
                elementConfig: {
                    options: [
                        {value: 'testvalue1', displayValue: 'testvalue1'},
                        {value: 'testvalue2', displayValue: 'testvalue2'}
                    ]
                },
                value: '',
                validation: {},
                valid: true
            },
            materialfee: {
                elementType: 'input',
                elementConfig: {
                    type: 'text',
                    placeholder: '0'
                },
                value: '',
                validation: {
                    required: true
                },
                valid: false,
                touched: false
            },
            packingfee: {
                elementType: 'input',
                elementConfig: {
                    type: 'text',
                    placeholder: '0'
                },
                value: '',
                validation: {
                    required: true,
                    minLength: 5,
                    maxLength: 5,
                    isNumeric: true
                },
                valid: false,
                touched: false
            },
            unpackingfee: {
                elementType: 'input',
                elementConfig: {
                    type: 'text',
                    placeholder: '0'
                },
                value: '',
                validation: {
                    required: true
                },
                valid: false,
                touched: false
            }
            },                    
        trowIsValid: false,
        loading: false
    }

 
  render() {

     const trowElementsArray = [];
        for (let key in this.state.tablerow) {
            trowElementsArray.push({
                id: key,
                config: this.state.tablerow[key]
            });
        }
        let trow = (
           <tr>
                {trowElementsArray.map(trowElement => (
                  <td className={classes.td} key= {trowElement.id}> 
                    <Input 
                        key={trowElement.id}
                        elementType={trowElement.config.elementType}
                        elementConfig={trowElement.config.elementConfig}
                        value={trowElement.config.value}
                        invalid={!trowElement.config.valid}
                        shouldValidate={trowElement.config.validation}
                        touched={trowElement.config.touched}
                        changed={(event) => this.inputChangedHandler(event, trowElement.id)} /> </td>
                ))}
           </tr>    
           
        );
         return (
            <div >
              
                {trow}
            </div>
        );

  }

}
export default ItemRow;
