import React, { Component } from "react";

import ItemRow from "./ItemRow/ItemRow";
import classes from './Table.css';

class Table extends Component {
  constructor(props) {
        super(props);
   
        this.state = {
          rows: []
        }
  

    this.colomns = [
      {
        heading: "Item",
        inputType: "Select"
      },
      {
        heading: "Material Fee",
        inputType: "currency"
      },
      {
        heading: "Packing Fee",
        inputType: "currency"
      },
      {
        heading: "Unpacking Fee",
        inputType: "currency"
      },
      {
        heading: "",
        inputType: ""
      }
    ];
  }

  
   handleDelete(row) {
    var index = this.state.rows.indexOf(row);
    this.state.rows.splice(index, 1);
    this.setState(this.state.rows);
  };
  renderHeader = () => {
    let header = [];
    this.colomns.forEach(colomn => {
      header.push(" " + colomn.heading);
    })
     return header.map((key, index) => {
      return <th key={index}>{key.toUpperCase()}</th>
    })

  };

 

  renderTable = () => {
    return (
      <table><thead><tr>{this.renderHeader()}</tr></thead><tbody><ItemRow onRowDel={this.handleDelete.bind(this)}/></tbody></table>
    );
  };

  render() {
      var rowDel = this.props.onRowDel;
      var row = this.rows.map(function(row) {
     
      return (<ItemRow  row={row} onDelEvent={rowDel.bind(this)} key={row.id}/>)
    });
    return (
      <div>
     
        {this.renderTable()} 
      
      </div>
    );
  }
}

export default Table;
