import React, { Component } from "react";

import classes from './Table.css';

class Table extends Component {
  constructor(props) {
        super(props);
        this.handleItemAdd = this.handleItemAdd.bind(this);
        this.state = {
          rows: []
        }
  

    this.colomns = [
      {
        heading: "Item",
        inputType: "Select"
      },
      {
        heading: "Material Fee",
        inputType: "currency"
      },
      {
        heading: "Packing Fee",
        inputType: "currency"
      },
      {
        heading: "Unpacking Fee",
        inputType: "currency"
      }
    ];
  }

  handleItemAdd = () => {
    const colomns = this.colomns.map(colomn => {
      return {
        name: colomn.heading,
        inputType: colomn.inputType,
        value: ""
      };
    });

    const newItem = {
      key: "asksakdaskd",
      colomns: colomns
    };

      
  //  this.setState({ rows: [...this.state.rows, newItem] });
  };

  handleDelete = item => {
    const filterdRows = this.state.rows.filter(row => row.key !== item.key);
    this.setState({ rows: filterdRows });
  };

  renderHeader = () => {
    let header = [];
    this.colomns.forEach(colomn => {
      header.push(" " + colomn.heading);
    })
    return header.map((key, index) => {
      return <th key={index}>{key.toUpperCase() }</th>
    })


  };



  renderFooter = () => {
     return <td colspan="4"><button type="button" handleClick={this.handleItemAdd()} className={classes.Button}>Add Item</button></td>
  };

  renderTable = () => {
    return (
      <table><thead><tr>{this.renderHeader()}</tr></thead><tbody><tr>{this.renderFooter()}</tr> </tbody> </table>
    );
  };

  render() {
    return (
      <div>
     
        {this.renderTable()} 
      
      </div>
    );
  }
}

export default Table;
