import React, { Component } from 'react';
import classes from '../../../Button/Button.css';
import Input from '../../../Input/Input';

  function onRowAdd(evt) {
    var id = (+ new Date() + Math.floor(Math.random() * 999999)).toString(36);
    var tablerowc = {
      itemname: "",
      materialfees: 0,
      packingfees: 0,
      unpackingfees:0
    }
    this.state.tablerow.push(tablerowc);
    this.setState(this.state.tablerow);

  }

class ItemRow extends Component {


    state = {
        tablerow: {
            itemname: {
                elementType: 'select',
                elementConfig: {
                    options: [
                        {value: 'itemvalue1', displayValue: 'itemvalue1'},
                        {value: 'itemvalue2', displayValue: 'itemvalue2'},
                        {value: 'itemvalue3', displayValue: 'itemvalue3'},
                        {value: 'itemvalue4', displayValue: 'itemvalue4'}
                    ]
                },
                value: '',
                validation: {},
                valid: true
            },
            materialfees: {
                elementType: 'input',
                elementConfig: {
                    type: 'text',
                    placeholder: '0'
                },
                value: '',
                validation: {
                    required: true
                },
                valid: false,
                touched: false
            },
            packingfees: {
                elementType: 'input',
                elementConfig: {
                    type: 'text',
                    placeholder: '0'
                },
                value: '',
                validation: {
                    required: true,
                    minLength: 5,
                    maxLength: 5,
                    isNumeric: true
                },
                valid: false,
                touched: false
            },
            unpackingfees: {
                elementType: 'input',
                elementConfig: {
                    type: 'text',
                    placeholder: '0'
                },
                value: '',
                validation: {
                    required: true
                },
                valid: false,
                touched: false
            }
            },                    
        trowIsValid: false,
        loading: false
    }

 
  render() {

     const trowElementsArray = [];
        for (let key in this.state.tablerow) {
            trowElementsArray.push({
                id: key,
                config: this.state.tablerow[key]
            });
        }
        let trow = (
           <tr>
                {trowElementsArray.map(trowElement => (
                  <td className={classes.td} key= {trowElement.id}> 
                    <Input 
                        key={trowElement.id}
                        elementType={trowElement.config.elementType}
                        elementConfig={trowElement.config.elementConfig}
                        value={trowElement.config.value}
                        invalid={!trowElement.config.valid}
                        shouldValidate={trowElement.config.validation}
                        touched={trowElement.config.touched}
                        changed={(event) => this.inputChangedHandler(event, trowElement.id)} /> </td>
                ))}
                <td><button onClick={(event) => this.removeRow(event, trow)}>X</button></td>
              
           </tr>    
           
        );
         return (
            <>
              
                {trow}
            </>
        );

  }

}
export default ItemRow;
