import React, { Component } from 'react';

import Table from './components/Table/Table';
import Button from './components/Button/Button';

import classes from './components/Button/Button.css';

//Columns defines table headings and properties to be placed into the table
let columns = [
  {
    heading: 'Item',
    inputType: 'Select'
  },
  {
    heading: 'Material Fee',
    inputType: 'currency'
  },
  {
    heading: 'Packing Fee',
    inputType: 'currency'
  },
  {
    heading: 'Unpacking Fee',
    inputType: 'currency'
  },
]



class App extends Component {

  render() {
    return (
      <>
        <Table
          columns={columns}

          propertyAsKey='heading' 
        />
   <Button className={classes.AddItem} clicked={this.props.onRowAdd}>Add Row</Button>
      </>
    );
  }
}
export default App;
