import React, { Component } from 'react';
import './App.css';
import Table from './components/Table/Table';

import Button from './components/Button/Button';

import classes from './components/Button/Button.css';



class App extends Component {

  render() {

    return (
      <div>

      <header className="App-header">  ReactJS Dynamically add/remove items from Table </header>
      <table />
     <button type="button" onClick={this.props.onRowAdd} className={classes.Button}>Add Item</button>

      </div>
    );

  }
}
export default App;
