import React, { Component } from 'react';

class ItemRow extends Component {
  onDelEvent() {
    this.props.onDelEvent(this.props.item);

  }
  render() {

    return (
      <tr className="eachRow">
        <EditableCell onItemTableUpdate={this.props.onItemTableUpdate} cellData={{
          "type": "Item",
          value: this.props.item.Item,
          id: this.props.item.id
        }}/>
        <EditableCell onItemTableUpdate={this.props.onItemTableUpdate} cellData={{
          type: "MaterialFee",
          value: this.props.item.MaterialFee,
          id: this.props.item.id
        }}/>
        <EditableCell onItemTableUpdate={this.props.onItemTableUpdate} cellData={{
          type: "qty",
          value: this.props.item.qty,
          id: this.props.item.id
        }}/>
        <EditableCell onItemTableUpdate={this.props.onProductTableUpdate} cellData={{
          type: "category",
          value: this.props.item.category,
          id: this.props.item.id
        }}/>
        <td className="del-cell">
          <input type="button" onClick={this.onDelEvent.bind(this)} value="X" className={classes.DelButton}/>
        </td>
      </tr>
    );

  }

}
export default ItemRow;
