import React, { Component } from 'react'

import ItemTable from './ItemTable/ItemTable';


class Table extends Component {

  constructor(props) {
    super(props);

    this.state = {};
    this.state.items = [
      {
        heading: 'Item',
        inputType: 'Select'
      },
      {
        heading: 'Material Fee',
        inputType: 'currency'
      },
      {
        heading: 'Packing Fee',
        inputType: 'currency'
      },
      {
        heading: 'Unpacking Fee',
        inputType: 'currency'
      },
    ]
  }

 

  render() {

    return (
      <div>
          <ItemTable onItemTableUpdate={this.listItemTable.bind(this) } onRowAdd={this.tableAddRowItem.bind(this) } onRowDel={this.tableRowDelItem.bind(this) } items={this.state.items} />     
      </div>
    );

  }

}

export default Table;
